(646, 1, '2017-05-01 06:00:51', '2017-05-01 06:00:51', '<p class=\"justify\">Marthe holds a Master’s degree in National and International Public Law from the University of Ghent. Hereafter she decided to delve into International Human Rights Law at Lund University in cooperation with the Raoul Wallenberg Institute in Sweden. During this time, she did a 3-month internship at the Office of the High Commissioner of Human Rights.(OHCHR) in Geneva, Switzerland. In October 2013 she started as a lawyer at the Antwerp Bar. In December 2016, she went to Greece as a volunteer lawyer in the refugee camp in Chios for a month. Now, Marthe is practicing immigration law in Antwerp and also collaborates with Just Rights law firm in Brussels.</p>', 'Marthe Van Beurden', '', 'inherit', 'closed', 'closed', '', '525-autosave-v1', '', '', '2017-05-01 06:00:51', '2017-05-01 06:00:51', '', 525, 'https://hrlfoundation.org/525-autosave-v1/', 0, 'revision', '', 0),
(647, 1, '2017-05-02 11:47:01', '2017-05-02 11:47:01', '[vc_row top_margin=\"1\" bottom_margin=\"1\"][vc_column][rev_slider_vc alias=\"our-donors\"][/vc_column][/vc_row][vc_row el_class=\"crowdfunding-video-banner\"][vc_column width=\"1/4\"][/vc_column][vc_column width=\"1/2\"][vc_custom_heading text=\"GIVE A VOICE to vulnerable victims of human rights violations before the United Nations\" font_container=\"tag:h2|font_size:18|text_align:center|color:%23eeeeee\" google_fonts=\"font_family:Roboto%3A100%2C100italic%2C300%2C300italic%2Cregular%2Citalic%2C500%2C500italic%2C700%2C700italic%2C900%2C900italic|font_style:500%20bold%20regular%3A500%3Anormal\" el_class=\"crowdfunding-video-title\"][vc_column_text]<iframe src=\"https://www.youtube.com/embed/Z-GprtYcDl8?rel=0&amp;showinfo=0\" width=\"560\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe>[/vc_column_text][/vc_column][vc_column width=\"1/4\"][/vc_column][/vc_row][vc_row][vc_column][vc_text_separator title=\"The Events\" color=\"black\" el_class=\"vision-header\"][sh_charity_events_new category=\"main\" sort_by=\"0\" sorting_order=\"0\"][/vc_column][/vc_row]', 'Our Campaigns & Events', '', 'inherit', 'closed', 'closed', '', '112-revision-v1', '', '', '2017-05-02 11:47:01', '2017-05-02 11:47:01', '', 112, 'https://hrlfoundation.org/112-revision-v1/', 0, 'revision', '', 0),
